mapper.select:
select count(*)
from board
where brand = #{bname}

SELECT bid, brand, bheader, btitle, bcontent, bauthor, bdate, bimg
from board
where bid = #{bno}

        select (select count(*) from board where brand = #{brand})-rn+1 as seq, bid, bheader, btitle, bauthor, bdate, bimg, bcontent from (
		select rownum as rn, bid, brand, bheader, btitle, bauthor, bdate, bimg, bcontent
		from (select * from board where board.brand=#{brand} order by bdate desc)
		) t where rn between (#{pageNum}-1)*10+1 and #{pageNum}*10
