mapper.update:
UPDATE MEMBER
SET MILEAGE = MILEAGE + #{mileage, jdbcType=INTEGER}
WHERE EMAIL = #{email, jdbcType=VARCHAR}

update.foreach:
UPDATE PRODUCTMANAGER.PRODUCT_STOCK
SET PSSTOCK = PSSTOCK - #{item.oicount, jdbcType=INTEGER}
WHERE PSID = #{item.psid, jdbcType=VARCHAR}

mapper.update:
	UPDATE ORDER_ITEM
	SET OSTATUS = '배송완료'
	WHERE PSID = #{psid}
   AND OID = #{oid}
